CREATE DATABASE db_ecommerce1USE db_ecommerce1CREATE TABLE tb_Usuarios(	Id_Usuarios INT PRIMARY KEY  NOT NULL,	Nome VARCHAR(100)  NULL,	Email VARCHAR(50)  NULL,	Senha VARCHAR(100) NULL,	Endereco VARCHAR(100) NULL,	Forma_de_pagamento VARCHAR (100) NULL )CREATE TABLE tb_Produtos(Id_Produtos INT PRIMARY KEY NOT NULL,Preco FLOAT  NULL,Quantidade INT  NULL,Categoria VARCHAR(50) NULL)CREATE TABLE tb_Entrega(Id_Entrega INT PRIMARY KEY NOT NULL,PrevisaoDeEntrega VARCHAR(50) NULL,FK_Id_Usuarios INT NULL,FK_Id_Produtos INT NULL,FOREIGN KEY (FK_Id_Usuarios) REFERENCES tb_Usuarios (Id_Usuarios),FOREIGN KEY (FK_Id_Produtos) REFERENCES tb_Produtos (Id_Produtos))

